import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./LoginPage.css";
import axios from 'axios';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

const LoginPage = ({setIsLoggedIn, authData}) => {
    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')
    const navigate = useNavigate();
    const [error, setError] = useState('')
    


    useEffect(() => {
        console.log(login)
        console.log(password)
    },[password, login]);
    


    const handleLoginChange = (e) => {
        setLogin(e.target.value)
    }
    const handlePasswordChange = (e) => {
        setPassword(e.target.value)
    }

    const handleLogIn = (e) => {
        e.preventDefault()
        setIsLoggedIn(true)
        // ---------------------------------------------
        const res = axios.post('http://localhost:3002/authArray', { id: 4, username: 'jjjj', password: '123' });
        console.log('121212121212: ',res.data)
        // --------------------------------------------------------
        let logCount = 0;
        let passCount = 0;
        for (let i = 0; i < authData.length; i++){

            if (login === authData[i].username) logCount++
            if (password === authData[i].password) passCount++

            console.log(authData)
        }
        if (logCount === 0 || passCount === 0) {
          setError("You have entered incorrect data! Try again")
          // document.querySelector('.loginFormInput').value = ''
          document.getElementById('outlined-login-input').value = ''
          document.getElementById('outlined-password-input').value = ''
        }
        else navigate("../hp", { replace: true })
    }

   return (
<>
      <div>

      </div>


    
      <form className="loginForm" onSubmit={handleLogIn}>
        <h2>Authorization</h2>
        <div className="auth__container">
        <div className="login__input">
            <TextField
              id="outlined-login-input"
              style = {{width: 350}}
              label="Login"
              type="login"
              autoComplete="current-password"
              onChange={handleLoginChange}
              onFocus={() => setError('')}
            />
        </div>
        <div className="login__password">
            <TextField
              id="outlined-password-input"
              style = {{width: 350}}
              label="Password"
              type="password"
              autoComplete="current-password"
              onChange={handlePasswordChange}
              onFocus={() => setError('')}
            />
        </div>
        <div className="login__button">
          <Button type="submit" variant="contained">Login</Button>
        </div>
        <div>
          <p className="errorMassage">{error}</p>
        </div>
        <div className="registration"><p onClick={() => navigate("../registration", { replace: true })}>Registration</p></div>
        </div>
      </form>
    
</>

  );
};


export default LoginPage
