const initialState = {
    items: {},
    totalPrice: 0,
    totalCount: 0
}

const getTotalPrice = arr => arr.reduce((sum, obj) => obj.price + sum, 0   )

 const cart = (state = initialState, action) => {

    if (action.type === 'PLUS_CART_ITEM') {

        const newItems = [
            ...state.items[action.payload].items,
            state.items[action.payload].items[0],
        ]
        return {
            ...state,
            items: {
                [action.payload]: {
                    items: newItems,
                    totalPrice: getTotalPrice(newItems)
                }
                
            }
        }
    }

    const getMinusPrice = arr => arr.reduce((sum, obj) => obj.price - sum, 0   )
    if (action.type === 'MINUS_CART_ITEM') {

        const newItems = [
            ...state.items[action.payload].items,
            state.items[action.payload].items[0],
        ]
        return {
            ...state,
            items: {
                [action.payload]: {
                    items: newItems,
                    totalPrice: getTotalPrice(newItems)
                }
                
            }
        }
    }
    

    if (action.type === 'REMOVE_1PIZZA') {


        const newItems = {
            ...state.items,
           }

        const currentTotalPrice = newItems[action.payload].totalPrice
        const currentTotalCount = newItems[action.payload].items.length
        console.log('---',  currentTotalCount)
        delete newItems[action.payload]

        return{
            ...state,
            items: newItems,
            totalPrice: state.totalPrice - currentTotalPrice,
            totalCount: state.totalCount - currentTotalCount,
         }



    }

    if (action.type === 'ADD_PIZZA_CART') {
        const currenPizzaItems = !state.items[action.payload.id] 
        ? [action.payload] 
        : [...state.items[action.payload.id].items, action.payload]

        const newItems = {
            ...state.items,
            [action.payload.id]: {
                items: currenPizzaItems,
                totalPrice: getTotalPrice(currenPizzaItems)
            }
        }

        const items = Object.values(newItems).map(obj => obj.items)
        const allPizzas = [].concat.apply([], items)
        const totalPrice = getTotalPrice(allPizzas)

        return {
            ...state,
            items: newItems,
            totalCount: allPizzas.length, 
            totalPrice: totalPrice
        }
    }
    else if (action.type ==='CLEAR_CART'){
        return {
            totalCount: 0,
            totalPrice: 0,
            items: {}}
    }
    return state
}



export default cart
