import axios from 'axios';

export const setPizzas = (items) => ({
    type: 'SET_PIZZAS',
    payload: items,
})

export const fetchPizzas = (sortBy, category) => (dispatch) => {
    dispatch({
      type: 'SET_LOADED',
      payload: false,
    });

    axios
      .get(
        `http://localhost:3001/pizzas?${category !== null ? `category=1` : ''}
        }`, 
      )
      .then(({ data }) => {
        dispatch(setPizzas(data));
      });
  };
