import React from 'react';
import styles from './app.module.css'
import { BrowserRouter, Route, Routes  } from 'react-router-dom'
import HomePage from '../../components/HomePage/HomePage';
import Cart from '../../components/Cart';
import axios from 'axios'
import { setPizzas as setPizzasCreator} from '../../redux/actions/pizzas';
import { setSortBy } from '../../redux/actions/filters';
import { useDispatch, useSelector } from 'react-redux';




const App = () => {

    const dispatch = useDispatch()
    const hranilishe = useSelector(({pizzasReducer, filtersReducer}) => {
        return {
            items: pizzasReducer.items,
            // sortBy: filtersReducer.sortBy
        }
    } )

    console.log(hranilishe)

    React.useEffect(() => {
        axios.get('http://localhost:3000/db.json').then( ({data}) => {
            // setPizzas(data.pizzas)
            dispatch(setPizzasCreator(data.pizzas));
        })
    }, [] )

    
    

    return (
        <div className={styles.container}>
            <BrowserRouter>
                <Routes>
                    <Route path="/" exact element={<HomePage pizzas={[]}/>} />
                    {/* <Route path="/" exact element={<HomePage pizzas={pizzas}/>} /> */}
                    <Route path="/cart" exact element={<Cart/>} />
                </Routes>
            </BrowserRouter>
        </div>
    );
};

export default App;

