// OneDark
import React from 'react';
import axios from 'axios'; 
import { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./HP.css";
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import ContactBlock from '../ContactBlock/ContactBlock';

const HP = ({setIsLoggedIn, authData, userID, axiosPost}) => {
  const inputNameRef = useRef(null);
  const inputPhoneNumberRef = useRef(null);
  const inputEmailRef = useRef(null);

  const [contactsData, setContactsData] = useState([])
  const [name, setName] = useState('')
  const [phoneNumber, setPhoneNumber] = useState('')
  const [email, setEmail] = useState('')

  useEffect(() => {
      axios.get('http://localhost:3002/contacts')
     .then(({data}) => {
      setContactsData(data)})
 },[])



    
    let navigate = useNavigate();

    // const handleChangeName = (e) => {
    //   setName(e.target.value)
    // }
    // const handleChangePhoneNumber = (e) => {
    //   setPhoneNumber(e.target.value)
    // }
    // const handleChangeEmail = (e) => {
    //   setEmail(e.target.value)
    // }

    console.log(name, phoneNumber, email)


    const handleLogOut = () => {
        navigate("../login", { replace: true })
        setIsLoggedIn(false)
    }

    const handleAddNewContact = (e) => {
        e.preventDefault();

        console.log("ID: ", contactsData[contactsData.length-1].id+1)
        console.log("Owner: ", userID)
        console.log(inputNameRef.current.value)
        console.log(inputEmailRef.current.value)
        console.log(inputPhoneNumberRef.current.value)
        axiosPost()

        // axios.post('http://localhost:3002/contacts', 
        // { id: contactsData[contactsData.length-1].id+1,
        //   ownerId: userID,
        //   name: inputNameRef.current.value,
        //   email: inputEmailRef.current.value, 
        //   phonenumber: inputPhoneNumberRef.current.value});

        // ---------------------------------------------
        // const res = axios.post('http://localhost:3002/authArray', { id: 4, username: 'jjjj', password: '123' });
        // console.log('121212121212: ',res.data)
        // --------------------------------------------------------
         
      }

    
    return (
      <>
        <AppBar position="static">
          <Toolbar>
             <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                 Contact Manager
              </Typography>
              <Button 
                variant="outlined"
                onClick={handleLogOut}
                color="inherit">
                    Logout
                </Button>
          </Toolbar>
        </AppBar>
      <div className="home__container">
        <div>
          {/* <div className="name__input">
            <input type="text" ref={inputRef} />
          </div> */}
          <div className="name__input">
              <TextField
                id="outlined-name-input"
                style = {{width: 350}}
                label="Name"
                type="name"
                inputRef={inputNameRef}
              />
          </div>
          <div className="mail__input">
              <TextField
                id="outlined-mail-input"
                style = {{width: 350}}
                label="Mail"
                type="mail"
                inputRef={inputEmailRef}
              />
          </div>
          <div className="phone__input">
              <TextField
                id="outlined-phone-input"
                style = {{width: 350}}
                label="Phone number"
                type="phone"
                inputRef={inputPhoneNumberRef}
              />
          </div>
          {/* <div className="login__button">
            <Button 
              variant="contained"  
              style = {{width: 200}} 
              endIcon={<SendIcon />}
              onClick={() => handleAddNewContact()}
            >Add contact</Button>
          </div> */}
          <div>
            <button onClick={handleAddNewContact}></button>
          </div>

        </div>
        <div>
          {
            contactsData.map((contact) =>   <ContactBlock contact={contact}/> )
          }
        </div>

      </div>
      </>



    



    );
};

export default HP;
