import React from 'react';
import { useState, useEffect, useRef } from "react";
import "./ContactBlock.css"
import pen from './icons/pen.svg'
import basket from './icons/basket.svg'
import axios from 'axios';
import EditPage from '../EditPage';
import { useNavigate } from "react-router-dom";


const ContactBlock = ({contact, contactsData}) => {
    const [contactId, setId] = useState(+localStorage.getItem('contact.id'))
    const [name, setName] = useState('Boris')
    const [email, setEmail] = useState('11111111@gmail.com')
    const [phonenumber, setPhonenumber] = useState('+777777777777777')

    let navigate = useNavigate();




    const handleDeleteContact = (id) =>{
        axios.delete(`http://localhost:3002/contacts/${id}`)
    }
    const handleEditClick = (id) =>{
        localStorage.setItem('contact.id', id)

        for (let i = 0; i < contactsData.length; i++) {
            if (contactId === contactsData[i].id) {
                setName(contactsData[i].name)
                setEmail(contactsData[i].email)
                setPhonenumber(contactsData[i].phonenumber)
            }
        }
        console.log("!!!!!!!!", contactId);
        console.log("!!!!!!!!",name);
        console.log(phonenumber);
        console.log(email);


       
        navigate("../ep", { replace: true }) 
        console.log("&&&&&&&&&&&&&&&&", contact);




    }


    let userID = +localStorage.getItem('userID')
    // console.log("OWNER",contact.ownerId);
    return (
        <>
            { userID === contact.ownerId && (
                <div className='contactsBlock__container'>
                        <div>
                            <div>{contact.ownerId}</div>
                            <div>{contact.name}</div>
                            <div>{contact.phonenumber}</div>
                            <div>{contact.email}</div>
                        </div>
                        <div className='icons__container'>
                             <img onClick={() => handleEditClick(contact.id)} className='penSVG' src={pen} alt="" />
                             <img onClick={() => handleDeleteContact(contact.id)} className='basketSVG' src={basket} alt="" />
                             {/* <img className='basketSVG' src={basket} alt="" /> */}
                        </div>
                </div>
            )}
        </>
    );
};

export default ContactBlock;
