    return (
        <div className={styles.container}>
            <div className={styles.categories}>
                <ul>
                    
                    <li className={styles.active} onClick={()=> onSelectItem(null)}>Все</li>
                    {items.map((name, index) => (
                        <li onClick={() => onSelectItem(index)}                                           
                            key={`${name}_${index}`} 
                        
