import React, { useState } from 'react';
import SortPopup from '../SortPopup/SortPopup';
import styles from './Navigation.module.css'

const Navigation = ({items, onClickProps}) => {
    const [activeItem, setActiveItem]  = useState(null)
    const [modalIsOpen, setModalIsOpen] = useState(false)
    // const [count, setCount]  = useState(0)


    const onSelectItem  = (index) => {
        setActiveItem(index)
    }

    return (
        <div className={styles.container}>
            <div className={styles.categories}>
                <ul>
                    
                    <li className={activeItem === null ? styles.active : ''} onClick={()=> onSelectItem(null)}>Все</li>
                    {items && items.map((name, index) => (
                        <li onClick={() => onSelectItem(index)}  
                            className={activeItem === index ? styles.active : ''}                                         
                            key={`${name}_${index}`} 
                        
                        >{name} </li>
                    ))}
                </ul>
            </div>
            <div>
                <button onClick={()=> setModalIsOpen(true)}>OpenModal</button>
                 <SortPopup trigger={modalIsOpen}/>
                 
            </div>
            
            
        </div>
    );
};

export default Navigation;
