// OneDark
import React from 'react';
import axios from 'axios'; 
import { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./EditPage.css";
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import ContactBlock from '../ContactBlock/ContactBlock';

const EditPage = ({contactsData}) => {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [phonenumber, setPhonenumber] = useState('')
    const [contactId, setId] = useState(+localStorage.getItem('contact.id'))

    const inputNameRef = useRef(null);
    const inputPhoneNumberRef = useRef(null);
    const inputEmailRef = useRef(null);
    let navigate = useNavigate();

    const handleEditContact = (e) => {
        e.preventDefault();
        // axios.put(`http://localhost:3002/contacts/${contactId}`, 
        // { id: contactId,
        //   ownerId: parseInt(localStorage.getItem('userID')),
        //   name: inputNameRef.current.value,
        //   email: inputEmailRef.current.value, 
        //   phonenumber: inputPhoneNumberRef.current.value});

        console.log(inputNameRef.current.value);
        console.log(inputEmailRef.current.value);
        console.log(inputPhoneNumberRef.current.value);
    }
  
    useEffect(() => {
        for (let i = 0; i < contactsData.length; i++) {
            if (contactId === contactsData[i].id) {
                setName(contactsData[i].name)
                setEmail(contactsData[i].email)
                setPhonenumber(contactsData[i].phonenumber)
            }
        }
    },[])


    return (
      <>
    <form className="loginForm" onSubmit={handleEditContact}>
      <div className="home__container">
        <div>
          <div className="name__input">
              {/* <input type="text" value={name}/> */}
              <TextField
                id="outlined-name-input"
                style = {{width: 350}}
                value={name}
                type="name"
                inputRef={inputNameRef}
              />
          </div>
          <div className="mail__input">
              <TextField
                id="outlined-mail-input"
                style = {{width: 350}}
                value={email}
                type="mail"
                inputRef={inputEmailRef}
              />
          </div>
          <div className="phone__input">
              <TextField
                id="outlined-phone-input"
                style = {{width: 350}}
                defaultValue={phonenumber}
                type="phone"
                inputRef={inputPhoneNumberRef}
              />
          </div>
          <div className="login__button">
            <Button 
              type="submit"
              variant="contained"  
              style = {{width: 200}} 
              endIcon={<SendIcon />}
            >Save</Button>
          </div>
        </div>
      </div>
    </form>
      </>
    );
};

export default EditPage;
