import React, {useState, useEffect} from 'react';
import styles from './app.module.css';
import { BrowserRouter, Route, Routes  } from 'react-router-dom';
import RegPage from '../../components/RegPage';
import LoginPage from '../../components/LoginPage';
import EditPage from '../../components/EditPage';
import HP from '../../components/HP';
import axios from 'axios';



const App = () => {
    const [authData, setAuth] = useState([])
    const [contactsData, setContactsData] = useState([])
    const [contactId, setId] = useState(+localStorage.getItem('contact.id'))

    useEffect(() => {
        axios.get('http://localhost:3002/auth.json')
       .then(({data}) => {
           setAuth(data.authArray)})

        axios.get('http://localhost:3002/contacts')
        .then(({data}) => {
        setContactsData(data)})   
   },[])

    const [isLoggedIn, setIsLoggedIn] = useState(false)

    return (
        <div className={styles.container}>
            <BrowserRouter>
                <Routes>
                    { contactId === null && (
                        <Route path="/login" exact element={<LoginPage 
                            setIsLoggedIn={setIsLoggedIn}
                            authData={authData}
                            />} />
                    )
                    }
                    
                        <Route 
                        path="/hp" 
                        exact element={<HP 
                            setIsLoggedIn={setIsLoggedIn} 
                            authData={authData}
                            contactsData={contactsData}
                            />} />
                
                    
                    <Route path="/registration" exact element={<RegPage authData={authData}/>} />
                    <Route path="/ep" exact element={<EditPage contactsData={contactsData}/>} />
                    <Route path="*" exact element={<HP/>} />
                </Routes>
            </BrowserRouter>
        </div>
    );
};

export default App;
