const initialState = {
    items: {},
    totalPrice: 0,
    totalCount: 0
}

export const cart = (state = initialState, action) => {
    if (action.type === 'SET_TOTAL_PRICE') {
        return {
            ...state,
            totalPrice: action.payload,
        }
    }
    if (action.type === 'SET_TOTAL_COUNT') {
        return {
            ...state,
            totalCount: action.payload,
        }
    }
    return state
}



export default cart
