import React from 'react';
import styles from './app.module.css'
import { BrowserRouter, Route, Routes  } from 'react-router-dom'
import HomePage from '../../components/HomePage/HomePage';
import Cart from '../../components/Cart';
import axios from 'axios'
import { setPizzas as setPizzasCreator} from '../../redux/actions/pizzas';
import { setSortBy } from '../../redux/actions/filters';
import { useDispatch, useSelector} from 'react-redux';

// Шаг 1 Используем dispatch
// Шаг 2 Используем useSelector


const App = () => {

    const dispatch = useDispatch()

    const { category, sortBy} = useSelector((state) =>  state.filterReducer )
    console.log(category, sortBy);

    React.useEffect(() => {
        axios.get('http://localhost:3001/pizzas?category=2').then( ({data}) => {
            
            dispatch(setPizzasCreator(data));
        })
    }, [category] )

    
    

    return (
        <div className={styles.container}>
            <BrowserRouter>
                <Routes>
                    <Route path="/" exact element={<HomePage/>} />
                    <Route path="/cart" exact element={<Cart/>} />
                </Routes>
            </BrowserRouter>
        </div>
    );
};

export default App;

