import React, {useState, useEffect} from 'react';
import styles from './app.module.css';
import { BrowserRouter, Route, Routes  } from 'react-router-dom';
import RegPage from '../../components/RegPage';
import LoginPage from '../../components/LoginPage';
import HP from '../../components/HP';
import axios from 'axios';



const App = () => {
    const [authData, setAuth] = useState([])
    const [userID, setUserId] = useState(null)

    useEffect(() => {
        axios.get('http://localhost:3002/auth.json')
       .then(({data}) => {
           setAuth(data.authArray)})
   },[])

   console.log('USER-ID = ', userID);

    const [isLoggedIn, setIsLoggedIn] = useState(false)



    return (
        <div className={styles.container}>
            <BrowserRouter>
                <Routes>
                    <Route path="/login" exact element={<LoginPage 
                        setIsLoggedIn={setIsLoggedIn}
                        authData={authData}
                        setUserId={setUserId}
                        />} />
                    <Route path="/hp" exact element={<HP setIsLoggedIn={setIsLoggedIn} authData={authData}/>} />
                    <Route path="/registration" exact element={<RegPage authData={authData}/>} />
                </Routes>
            </BrowserRouter>
        </div>
    );
};

export default App;
