import { useState, useEffect } from "react";
// import { useNavigate } from "react-router-dom";
import "./RegPage.css";
import axios from 'axios';
import Button from '@mui/material/Button';
import { useNavigate } from "react-router-dom";

const RegPage = ({authData}) => {
  let navigate = useNavigate();
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
    
  console.log(authData)
  console.log(error)


  const handleLoginChange = (e) => {
    setLogin(e.target.value)
  }

  const handlePasswordChange = (e) => {
    setPassword(e.target.value)
  }

    const handleRegistration = (e) => {
      e.preventDefault()
      for (let i = 0; i < authData.length; i++){
        if (login === authData[i].username) {
          setError('Имя с таким пользователем занято!')
        }
      }
    }
    
    const handleCancelClick = () => {
      navigate("../login", { replace: true })
    }

   return (
    <h1>
      <form 
      className="loginForm" 
      onSubmit={handleRegistration}
      >
        <div>Registration</div> 
        <div>
          <input
            className="loginFormInput"
            type="text"
            placeholder="Имя пользователя"
            required
            onChange={handleLoginChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
          <input
            className="loginFormInput"
            id="pass"
            type="password"
            placeholder="Ваш пароль"
            required
            onChange={handlePasswordChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
        <div className='buttonReg__container'>
                <div className="cancelReg__button">
                    <Button 
                    type="submit"
                    variant="contained"  
                    style = {{width: 100}} 
                    onClick = {handleCancelClick}
                    >Cancel</Button>
                </div>
                <div className="Reg__button">
                    <Button 
                    type="submit"
                    variant="contained"  
                    style = {{width: 100}} 
                    >Save</Button>
                </div>
          </div>
          <p className="errorMassage">{error}</p>
        </div>
      </form>
    </h1>
  );
};


export default RegPage
