import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./LoginPage.css";
import axios from 'axios';

const LoginPage = ({setIsLoggedIn}) => {
    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')
    const navigate = useNavigate();
    const [authData, setAuth] = useState([])
    


    useEffect(() => {
        console.log(login)
        console.log(password)
    },[password, login]);
    
    useEffect(() => {
         axios.get('http://localhost:3002/auth.json')
        .then(({data}) => {
            setAuth(data.authArray)})
    },[])


    const handleLoginChange = (e) => {
        setLogin(e.target.value)
        localStorage.setItem("Login", login)
    }
    const handlePasswordChange = (e) => {
        setPassword(e.target.value)

    }

    const handleLogIn = (e) => {
        e.preventDefault()
        setIsLoggedIn(true)
        
        
        for (let i = 0; i < authData.length; i++){
            console.log(authData[i])
        }
        // authData.forEach(el => {
        //     console.log(authData[el])
        // })



        navigate("../hp", { replace: true })



        // if (login === 'admin') {
        //   if (password === '123456') setIsAdmin(true);
        //   else {
        //     alert('Введите правильный логин или пароль!');
        //     return false
        //   }
        }

   return (
    <h1>
      <form className="loginForm" onSubmit={handleLogIn}>
        <h2>Авторизация</h2>
        <div>
          <input
            className="loginFormInput"
            type="text"
            placeholder="Логин"
            required
            onChange={handleLoginChange}
          />
        </div>
        <div>
          <input
            className="loginFormInput"
            type="password"
            placeholder="Пароль"
            required
            onChange={handlePasswordChange}
          />
        </div>
        <div>
          <button 
            className="blackBtn" 
            type="submit">
            Войти
          </button>
        </div>
      </form>
    </h1>
  );
};


export default LoginPage
