import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./LoginPage.css";
import axios from 'axios';


import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

const LoginPage = ({setIsLoggedIn, authData}) => {
    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')
    const navigate = useNavigate();
    const [error, setError] = useState('')
    


    useEffect(() => {
        console.log(login)
        console.log(password)
    },[password, login]);
    


    const handleLoginChange = (e) => {
        setLogin(e.target.value)
    }
    const handlePasswordChange = (e) => {
        setPassword(e.target.value)
    }

    const handleLogIn = (e) => {
        e.preventDefault()
        setIsLoggedIn(true)
        // ---------------------------------------------
        const res = axios.post('http://localhost:3002/authArray', { id: 4, username: 'jjjj', password: '123' });
        console.log('121212121212: ',res.data)
        // --------------------------------------------------------
        let logCount = 0;
        let passCount = 0;
        for (let i = 0; i < authData.length; i++){

            if (login === authData[i].username) logCount++
            if (password === authData[i].password) passCount++

            console.log(authData)
        }
        if (logCount === 0 || passCount === 0) {
          setError("Вы ввели неверные данные! Попробуйте снова")
          document.querySelector('.loginFormInput').value = ''
          document.getElementById('pass').value = ''
        }
        else navigate("../hp", { replace: true })
    }

   return (
<>
    <Box
      component="form"
      sx={{
        '& .MuiTextField-root': { m: 1, width: '25ch' },
      }}
      noValidate
      autoComplete="off"
    >
        <TextField
          id="outlined-password-input"
          label="Password"
          type="password"
          autoComplete="current-password"
        />
        <TextField
          id="outlined-password-input"
          label="Password"
          type="password"
          autoComplete="current-password"
        />
    </Box>


    <h1>
      <form className="loginForm" onSubmit={handleLogIn}>
        <h2>Авторизация</h2>
        <div>
          <input
            className="loginFormInput"
            type="text"
            placeholder="Логин"
            required
            onChange={handleLoginChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
          <input
            className="loginFormInput"
            id="pass"
            type="password"
            placeholder="Пароль"
            required
            onChange={handlePasswordChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
          <button 
            className="blackBtn" 
            type="submit">
            Войти
          </button>
          <p className="errorMassage">{error}</p>
        </div>
        <div><p onClick={() => navigate("../registration", { replace: true })}>Регистрация</p></div>
      </form>
    </h1>
</>

  );
};


export default LoginPage
