import React from 'react';
import styles from './PizzaList.module.css'
import CN from 'classnames'
import { useSelector } from 'react-redux';
import './style.css'
import { CSSTransition } from 'react-transition-group';


// import pizza3 from '../../data/img/image_3.png'
// import pizza4 from '../../data/img/image_4.png'
// import pizza5 from '../../data/img/image_5.png'

const PizzaList = ({onClickProps}) => {
    
    //Получаем данные из хранилища====================================
    const pizzas = useSelector((state) =>  state.pizzasReducer.items )
    //Данные получены!!!===================================================

    const [show, setShow] = React.useState(false)
    const [activeType, setActiveType] = React.useState(0)
    const [sizes, setSizes] = React.useState(0)
    const types = ['тонкое', 'традиционное']
    const availibleSize = [26, 30, 40]

    const onSelectType = (index) => {
        setActiveType(index)
        onClickProps(index)
    }
    const onSelectSize = (index) => {
        setSizes(index)
    }


    // const blocksAnimation = () => {
    //     setShow(!show)
    // }
    // blocksAnimation()

    return (
        
        <div className={styles.container}>

            {
                pizzas.map(pizzaItem => {


                    return (
                        
                        <CSSTransition 
                        in={!show}
                        timeout={500}
                        classNames="pizzaList_animation"                    
                        unmountOnExit>

                        <div className="animation">
                        <div className={styles.pizza_item}>
                        <img className={styles.pizza_img} src={pizzaItem.imageUrl} alt="" />
                        <div className={styles.pizza_name}>{pizzaItem.name}</div>
                        <div className={styles.pizza_costs}>от {pizzaItem.price} ₽</div>
                        <div className={styles.button__container}>
                            <div className={styles.button_block1}>
                                {types.map((type, index) => {
                                    return(
                                        <button 
                                            onClick={() => onSelectType(index)}
                                            className={CN(
                                                styles.button1, 
                                                pizzaItem.types[index] === 0 ? styles.disabled : ''
                                                )}
                                        >{type}</button>
                                        // <button className={CN(styles.button1, pizzaItem.types[0] === 0 ? styles.disabled : '')}>{type}</button>
                                    )
                                }
                                )}
                               
                            </div>
                            <div className={styles.button_block2}>
                            {availibleSize.map((size, index) => {
                                    return(
                                        <button 
                                            onClick={() =>  onSelectSize(index) }
                                            className={CN(
                                                styles.button2, 
                                                pizzaItem.sizes[index] !== size ? styles.disabled : ''
                                                )}
                                        >{size} см.</button>
                                        // <button className={CN(styles.button1, pizzaItem.types[0] === 0 ? styles.disabled : '')}>{type}</button>
                                    )
                                }
                                )}




                                {/* <button className={styles.button2}>26 см.</button>
                                <button className={CN(styles.button2, styles.disabled)}>30 см.</button>
                                <button className={styles.button2}>40 см.</button> */}
                            </div>
                        </div>
                        <div className={styles.block4}>
                            <div className={styles.pizza_costs}></div>
                            <div><button className={styles.button_add}>+ Добавить <span>2</span></button>   </div>
                        </div>
                    </div>

                        </div>




                        </CSSTransition>
                        )
                })

            }

                                    {/* {pizzas.types.map(pizzaType => {
                                        return (
                                            <div className={styles.button_block1}>
                                                <button className={styles.button1}>{pizzaType}</button>
                                            </div>
                                        )
                                    }) */}




            
           
        </div>
    );
};

export default PizzaList;
