// OneDark
import React from 'react';
import axios from 'axios'; 
import { useState, useEffect, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "./EditPage.css";
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import SendIcon from '@mui/icons-material/Send';
import ContactBlock from '../ContactBlock/ContactBlock';

const EditPage = ({contactsData}) => {
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [phonenumber, setPhonenumber] = useState('')

  const inputNameRef = useRef(null);
  const inputPhoneNumberRef = useRef(null);
  const inputEmailRef = useRef(null);
    let navigate = useNavigate();

    const handleEditContact = (e) => {
        e.preventDefault();
        // console.log(userID)

        // axios.put('http://localhost:3002/contacts/', 
        // { id: contactsData[contactsData.length-1].id+1,
        //   ownerId: parseInt(localStorage.getItem('userID')),
        //   name: inputNameRef.current.value,
        //   email: inputEmailRef.current.value, 
        //   phonenumber: inputPhoneNumberRef.current.value});
    }
  
    useEffect(() => {
        for (let i = 0; i < contactsData.length; i++) {
            if (parseInt(localStorage.getItem('contact.id')) === contactsData[i].id) {
                setName(contactsData[i].name)
                setEmail(contactsData[i].email)
                setPhonenumber(contactsData[i].phonenumber)
                console.log("2323",contactsData[i].name)
                console.log(contactsData[i].email)
                console.log(contactsData[i].phonenumber)
            }
        }
    },[])
    
    return (
      <>
    <form className="loginForm" onSubmit={handleEditContact}>
      <div className="home__container">
        <div>

          <div className="name__input">
              <TextField
                id="outlined-name-input"
                style = {{width: 350}}
                defaultValue={name}

                label="Name"
                type="name"
                inputRef={inputNameRef}
              />
          </div>
          <div className="mail__input">
              <TextField
                id="outlined-mail-input"
                style = {{width: 350}}
                defaultValue={email}

                label="Mail"
                type="mail"
                inputRef={inputEmailRef}
              />
          </div>
          <div className="phone__input">
              <TextField
                id="outlined-phone-input"
                style = {{width: 350}}
                defaultValue={phonenumber}
                label="Phone number"
                type="phone"
                inputRef={inputPhoneNumberRef}
              />
          </div>
          <div className="login__button">
            <Button 
              type="submit"
              variant="contained"  
              style = {{width: 200}} 
              endIcon={<SendIcon />}
              // onClick={handleAddNewContact}
            >Save</Button>
          </div>
        </div>
      </div>
    </form>
      </>
    );
};

export default EditPage;
