// import { useNavigate } from "react-router-dom";
import "./RegPage.css";
import axios from 'axios';

const RegPage = ({authData}) => {
  const [login, setLogin] = useState('')
  const [password, setPassword] = useState('')
  const [error, setError] = useState('')
    
  console.log(authData)
  console.log(error)


  const handleLoginChange = (e) => {
    setLogin(e.target.value)
  }


useEffect(() => {
  console.log(login)
  console.log(password)

  for (let i = 0; i < authData.length; i++){
    if (login === authData[i].username) {
      setError('Имя с таким пользователем занято!')
      console.log('authData[i].username', authData[i].username)
    }
    
  }

},[password, login]);


    const handleRegistration = () => {
        


        // setIsLoggedIn(true)
        // ---------------------------------------------
        // const res = axios.post('http://localhost:3002/authArray', { id: 4, username: 'jjjj', password: '123' });
