        // --------------------------------------------------------

        // if (logCount === 0 || passCount === 0) {
        //   setError("Вы ввели неверные данные! Попробуйте снова")
        //   document.querySelector('.loginFormInput').value = ''
        //   document.getElementById('pass').value = ''
        // }
        // else navigate("../login", { replace: true })
      }




   return (
    <h1>
      <form 
      className="loginForm" 
      onSubmit={handleRegistration}
      >
        <h2>Регистрация</h2>
        <div>
          <input
            className="loginFormInput"
            type="text"
            placeholder="Имя пользователя"
            required
            onChange={handleLoginChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
          <input
            className="loginFormInput"
            id="pass"
            type="password"
            placeholder="Ваш пароль"
            required
            // onChange={handlePasswordChange}
            onFocus={() => setError('')}
          />
        </div>
        <div>
          <button 
            className="blackBtn" 
            type="submit">
            Войти
          </button>
          <p className="errorMassage">{error}</p>
        </div>
      </form>
    </h1>
  );
};


export default RegPage
