import React from 'react';
import styles from '../PizzaList.module.css'
import CN from 'classnames'

const PizzaBlock = ({pizzaItem, types, availibleSize}) => {
    const [activeType, setActiveType] = React.useState(null)
    const [sizes, setSizes] = React.useState(null)

    const onSelectSize = (index) => {
        setSizes(index)
        console.log(index);
        console.log(sizes);
     }

    const onSelectType = (index) => {
        setActiveType(index)
        // console.log(activeType);
    }
    // console.log(pizzaItem);
    


                    let obj = { 
                        id: pizzaItem.id,
                        imageUrl: pizzaItem.imageUrl,
                        name: pizzaItem.name,
                        price: pizzaItem.price,
                        size: sizes,
                        type: activeType,
                    }
                    const handeleAddPizzaCart = () => {
                        return console.log(obj.size)
                    }
                    
                    return (

                        <div className={styles.animation}>
                        <div className={styles.pizza_item}>
                        <img className={styles.pizza_img} src={pizzaItem.imageUrl} alt="" />
                        <div className={styles.pizza_name}>{pizzaItem.name}</div>
                        <div className={styles.pizza_costs}>от {pizzaItem.price} ₽</div>
                        <div className={styles.button__container}>
                            <div className={styles.button_block1}>
                                {types.map((type, index) => {
                                    return(
                                        <div>
                                        <button 
                                            onClick={onSelectType(index)}
                                            className={CN(
                                                styles.button1, 
                                                activeType === index ? styles.active : '',
                                                pizzaItem.types[index] === 0 ? styles.disabled : '',
                                            )}
                                        >{type}</button>
                                        </div>
                                    )
                                }
                                )}
                               
                            </div>
                            <div className={styles.button_block2}>
                            {availibleSize.map((size, index) => {
                                    return(
                                        <div>
                                        <button 
                                            onClick={onSelectSize(index) }
                                            className={CN(
                                                styles.button2, 
                                                sizes === 1 ? styles.active : '',
                                                pizzaItem.sizes[index] === 0 ? styles.disabled : '',
                                                )}
                                        >{size} см.</button>
                                        </div>
                                    )
                                }
                                )}


                            </div>
                        </div>
                        <div className={styles.block4}>
                            <div className={styles.pizza_costs}></div>
                            <div><button className={styles.button_add}
                                // onClick={() => dispatch(setAddCart(index))}
                                // onClick={() => console.log(obj)}
                                onClick={handeleAddPizzaCart}
                            
                            >+ Добавить <span>2</span></button>   </div>
                        </div>
                    </div>

                        </div>





                        )

};

export default PizzaBlock;
