import React, { useState, useEffect, useRef } from 'react';
import styles from './Navigation.module.css'
import CN from 'classnames';
import { useSelector, useDispatch } from 'react-redux';
import { setPizzas as setPizzasCreator} from '../../redux/actions/pizzas';
import axios from 'axios'

const Navigation = ({ onClickProps }) => {

    // const {category} = useSelector((state) =>  state.filterReducer )
    const { category, sortBy} = useSelector((state) =>  state.filterReducer )
    const dispatch = useDispatch()
    React.useEffect(() => {
        axios.get(`http://localhost:3001/pizzas?category=${category}`).then( ({data}) => {
            dispatch(setPizzasCreator(data));
        })
    }, [category] )



    const [activeItemPopup, setActiveItemPopup] = useState(0)
    const [visiblePopup, setVisiblePopup] = useState(false)

    const sortRef = useRef(null)



    const clickOutside = (e) => {
        if (!e.path.includes(sortRef.current)) {
            setVisiblePopup(false)
        }
    }

    useEffect(() => {
        document.body.addEventListener('click', clickOutside)
        // console.log(sortRef.current);
    }, [])




    const onSelectItem = (index) => {
        onClickProps(index)
    }
    const onSelectItemPopup = (index) => {
        setActiveItemPopup(index)
        setVisiblePopup(false)
    }



    const itemsPopup = [
        {name: 'По популярности', type: 'popular'}, 
        {name: 'По цене', type: 'price'}, 
        {name: 'По алфавиту', type: 'alphabet'}
    ]

    const items=['Мясные','Вегетарианская','Гриль','Острые','Закрытые']

    const activeLabel = itemsPopup[activeItemPopup].name
    return (
        <div className={styles.container}>
            <div className={styles.categories}>
                <ul>

                    <li 
                        className={category === null ? styles.active : ''} 
                        onClick={() => onSelectItem(null)}
                    >Все</li>
                    {items && items.map((name, index) => (
                        <li onClick={() => onSelectItem(index)}
                            className={category === index ? styles.active : ''}
                            key={`${name}_${index}`}

                        >{name} </li>
                    ))}
                </ul>
            </div>
            
            <div
                ref={sortRef}
                className={styles.sort_button}>
                Сортировка по: <span  onClick={() => setVisiblePopup(!visiblePopup)}>{activeLabel}</span>
                {visiblePopup && (
                    <div className={styles.button_popup}>
                        <ul >
                            
                        {itemsPopup && itemsPopup.map((obj, index) => (
                            <li 
                            className={CN(styles.liPopup, activeItemPopup === index ? styles.activePopup : '')}
                            onClick={() => onSelectItemPopup(index)}
                            // className={activeItemPopup === index ? classes.activePopup : ''}
                            key={`${obj.name}_${index}`}
                            >{obj.name}
                            </li>
                        ))}   
                           
                        </ul>
                    </div>
                )
                }


            </div>


        </div>
    );
};

export default Navigation;
