import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./LoginPage.css";
import axios from 'axios';

const LoginPage = ({setIsLoggedIn}) => {
    const [login, setLogin] = useState('')
    const [password, setPassword] = useState('')
    const navigate = useNavigate();
    const [authData, setAuth] = useState([])
    const [error, setError] = useState('')
    


    useEffect(() => {
        console.log(login)
        console.log(password)
    },[password, login]);
    
    useEffect(() => {
         axios.get('http://localhost:3002/auth.json')
        .then(({data}) => {
            setAuth(data.authArray)})
    },[])


    const handleLoginChange = (e) => {
        setLogin(e.target.value)
    }
    const handlePasswordChange = (e) => {
        setPassword(e.target.value)
    }

    const handleLogIn = (e) => {
        e.preventDefault()
        setIsLoggedIn(true)
        
        let logCount = 0;
        let passCount = 0;
        for (let i = 0; i < authData.length; i++){

            if (login === authData[i].username) logCount++
            if (password === authData[i].password) passCount++

            console.log(authData)
        }
        if (logCount === 0 || passCount === 0) {
          setError("Вы ввели неверные данные! Попробуйте снова")
          document.querySelector('.loginFormInput').value = ''
          document.getElementById('pass').value = ''
        }
        else navigate("../hp", { replace: true })

        // if (login === 'admin') {
        //   if (password === '123456') setIsAdmin(true);
        //   else {
        //     alert('Введите правильный логин или пароль!');
        //     return false
        //   }
        }

   return (
    <h1>
      <form className="loginForm" onSubmit={handleLogIn}>
        <h2>Авторизация</h2>
        <div>
          <input
            className="loginFormInput"
            type="text"
            placeholder="Логин"
            required
            onChange={handleLoginChange}
          />
        </div>
        <div>
          <input
            className="loginFormInput"
            id="pass"
            type="password"
            placeholder="Пароль"
            required
            onChange={handlePasswordChange}
          />
        </div>
        <div>
          <button 
            className="blackBtn" 
            type="submit">
            Войти
          </button>
          <p>error</p>
        </div>
      </form>
    </h1>
  );
};


export default LoginPage
